                    x: [],
                    y: [],
                    name: plotConfig.trace2Title,
                    yaxis: 'y2',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: this.plotConfig.trace2Color,
                        size: 10,
                        symbol: 'square'
                    }        

                };
            }
            addTracePoints(trace1Pt, trace2Pt)
            {
//                var now = (new Date().getTime() - this.plotStartTime) / 1000;
                var now = new Date().getTime();
                var plotBeginning = now - this.plotConfig.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace1.x.push(now);
                this.trace1.y.push(trace1Pt);
                this.trace2.x.push(now);
                this.trace2.y.push(trace2Pt);
                Plotly.newPlot(this.plotConfig.htmlId, [this.trace1, this.trace2], this.layout);
            }
        }

