        function loopDelay()
        {
            $('#loopDelayButtonId').hide();
            if (isNaN($('#loopDelayInputId').val()))
            {
                loopDelayChanged = false;
                return;
            }
            setScalarValue(Number($('#loopDelayInputId').val()), 'loopDelay');
            loopDelayChanged = false;
        }

