// StripChart Widget
        class StripChart
        {
           constructor(plotConfig) 
            {
                this.plotConfig = plotConfig;
                this.plotStartTime = new Date().getTime();
                this.layout =
                {
                    showlegend  : true,
                    legend      : { x: 0, y: 1.15},
                    height      : plotConfig.height,
                    width       : plotConfig.width,
                    xaxis       :
                    {
                        title: 'Seconds since ' + new Date(this.plotStartTime).toLocaleString(),
                    },
                    yaxis:
                    {
                        title           : plotConfig.trace1Title,
                        titlefont       : {color: plotConfig.trace1Color},
                        tickfont        : {color:plotConfig.trace1Color},
                        gridcolor       : plotConfig.trace1GridColor,
                        zerolinecolor   : plotConfig.trace1GridColor,
                        linecolor       : plotConfig.trace1GridColor,
                        range: [0, 2]

                    },
                    yaxis2:
                    {
                        title           : plotConfig.trace2Title,
                        titlefont       : {color: plotConfig.trace2Color},
                        tickfont        : {color:plotConfig.trace2Color},
                        gridcolor       : plotConfig.trace2GridColor,
                        zerolinecolor   : plotConfig.trace2GridColor,
                        linecolor       : plotConfig.trace2GridColor,
                        overlaying: 'y',
                        side: 'right',
                        range: [0, 2]
                    },
                    margin: 
                        {
                            t: 30, //top margin
        //                    l: 0, //left margin
        //                    r: 0, //right margin
        //                    b: 20 //bottom margin
                        },
                };
                this.trace1 = 
                {
                    x: [],
                    y: [],
                    name: plotConfig.trace1Title,
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: this.plotConfig.trace1Color}

                };
                this.trace2 = 
                {
                    x: [],
                    y: [],
                    name: plotConfig.trace2Title,
                    yaxis: 'y2',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: this.plotConfig.trace2Color}

                };
            }
            addTracePoints(trace1Pt, trace2Pt)
            {
                var now = (new Date().getTime() - this.plotStartTime) / 1000;
                var plotBeginning = now - this.plotConfig.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace1.x.push(now);
                this.trace1.y.push(trace1Pt);
                this.trace2.x.push(now);
                this.trace2.y.push(trace2Pt);
                Plotly.newPlot(this.plotConfig.htmlId, [this.trace1, this.trace2], this.layout);
            }
        }

